// Dropdown
let dropdowns = document.querySelectorAll('.dropdown');

if (dropdowns) {
    dropdowns.forEach(function (dropdown) {
        let dropdown_button = dropdown.querySelector('.dropdown__button');
        let dropdown_button_text = dropdown_button.querySelector('.button-dropdown__text');
        let dropdown_list = dropdown.querySelector('.dropdown__list');
        let dropdown__items = dropdown.querySelectorAll('.dropdown__list-item');
        let dropdown_input_hidden = dropdown.querySelector('.dropdown__input-hidden');
        let dropdown_list_block = dropdown.querySelector('.dropdown__list-block');
        let dropdown_search = dropdown.querySelector('.dropdown__search-input');
        let dropdown_search_message = dropdown.querySelector('.dropdown__search-message');

        dropdown_button.addEventListener('click', function (event) {
            event.preventDefault();

            dropdown_list.classList.toggle('active');

            if (dropdown_list_block) {
                dropdown_list_block.classList.toggle('active');
            }

            if (dropdown_search) {
                dropdown_search.value = '';
                dropdownSearch();
            }
        })

        dropdown__items.forEach(function (dropdown__item) {
            dropdown__item.addEventListener('click', function (event) {
                event.preventDefault();

                dropdown__items.forEach(function (item) {
                    item.classList.remove('selected');
                })

                this.classList.add('selected');
                dropdown_button_text.textContent = this.textContent;
                dropdown_input_hidden.value = this.dataset.dropdown;
                dropdown_list.classList.remove('active');

                if (dropdown_list_block) {
                    dropdown_list_block.classList.remove('active');
                }

                if (dropdown_button.classList.contains('input-label')) {
                    dropdown_button.classList.add('change');
                    dropdown_button.classList.add('change-btn');
                    orderDate();
                }

                if (dropdown_search) {
                    dropdown_search.value = '';
                    dropdownSearch();
                }
            })
        })

        document.addEventListener('click', function (event) {
            if (!event.target.closest('.dropdown') && dropdown_list.classList.contains('active')) {
                dropdown_list.classList.remove('active');

                if (dropdown_list_block) {
                    dropdown_list_block.classList.remove('active');
                }

                if (dropdown_search) {
                    dropdown_search.value = '';
                    dropdownSearch();
                }
            }
        })

        if (dropdown_search) {
            dropdown_search.addEventListener('input', dropdownSearch);
        }

        function dropdownSearch() {
            if (dropdown__items) {
                dropdown__items.forEach(function (item) {
                    item.classList.remove('hide');

                    let search_value = dropdown_search.value.toLowerCase();
                    let item_value = item.textContent.toLowerCase();

                    if (!item_value.includes(search_value)) {
                        item.classList.add('hide');
                    }
                })
            }

            if (dropdown_search_message) {
                let items_hide = dropdown.querySelectorAll('.hide');

                if (items_hide.length == dropdown_list.children.length) {
                    dropdown_search_message.classList.add('active');
                } else {
                    dropdown_search_message.classList.remove('active');
                }
            }
        }
    })
}

