// Basket
let total_basket = document.querySelector('.total-bottom-modal-basket__sum');
let cards_product = document.querySelectorAll('.cart-product');

if (cards_product) {
    cards_product.forEach(function (card_product) {
        let btn_plus = card_product.querySelector('.button-primary.plus');
        let btn_minus_desktop = btn_plus.nextElementSibling;
        let btns_minus = card_product.querySelectorAll('.button-primary.minus');
        let product_input = card_product.querySelector('input');
        let product_total_sum = card_product.querySelector('.cart-product__cost-sum');
        let product_price = card_product.querySelector('.cart-product__cost-price');

        btn_plus.addEventListener('click', function (event) {
            event.preventDefault();

            product_input.value = +product_input.value + 1;
            btn_minus_desktop.style.display = 'block';
            totalBasket();
            totalProduct();
        })

        btns_minus.forEach(function (btn_minus) {
            btn_minus.addEventListener('click', function (event) {
                event.preventDefault();

                if (product_input.value > 1) {
                    product_input.value = +product_input.value - 1;
                    totalBasket();
                    totalProduct();

                    if (product_input.value == 1) {
                        btn_minus_desktop.style.display = 'none';
                    }
                }
            })
        })

        window.addEventListener('resize', totalProduct);

        function totalProduct() {
            if (document.documentElement.clientWidth < 993) {
                product_total_sum.textContent = (parseInt(product_price.textContent) * product_input.value).toFixed(2) + 'грн';
            } else {
                product_total_sum.textContent = product_price.textContent;
            }
        }
    })

    totalBasket();

    function totalBasket() {
        total_basket.textContent = 0;

        cards_product.forEach(function (card_product) {
            let product_input = card_product.querySelector('input');
            let product_price = card_product.querySelector('.cart-product__cost-price');

            total_basket.textContent = +total_basket.textContent + parseInt(product_price.textContent) * product_input.value;
        })
    }
}

