// Header Additionally Fixed
let header_additionally = document.querySelector('.header__additionally');
let header = document.querySelector('.header');
let header_additionally_height;
let catalog_top = document.querySelector('.catalog__top');
let breadcrumbs = document.querySelector('.breadcrumbs');
let product_top = document.querySelector('.product__top');
let service_menu = document.querySelector('.service__menu');
let top_menu = document.querySelector('.top-menu');

window.addEventListener('scroll', function (event) {
    if (pageYOffset > 0) {
        header.classList.add('fixed');
        header_additionally_height = header_additionally.getBoundingClientRect().height;
        header.style.paddingTop = header_additionally_height + 'px';

    } else {
        header.classList.remove('fixed');
        header.style.paddingTop = 0;
    }

    if (pageYOffset >= header.getBoundingClientRect().height - header_additionally_height) {
        header.classList.add('show-basket');

        if (catalog_top) {
            catalog_top.classList.add('fixed');
            catalog_top.style.top = header_additionally_height + 'px';

            if (breadcrumbs) {
                breadcrumbs.style.display = 'none';
            }
        }

        if (product_top) {
            product_top.classList.add('fixed');
            product_top.style.top = header_additionally_height + 'px';

            if (breadcrumbs) {
                breadcrumbs.style.display = 'none';
            }
        }

        if (service_menu) {
            service_menu.classList.add('fixed');
            service_menu.style.top = header_additionally_height + 'px';

            if (breadcrumbs) {
                breadcrumbs.style.display = 'none';
            }
        }

        if (top_menu) {
            top_menu.classList.add('fixed');
            top_menu.style.top = header_additionally_height + 'px';

            if (breadcrumbs) {
                breadcrumbs.style.display = 'none';
            }
        }
    } else {
        header.classList.remove('show-basket');

        if (catalog_top) {
            catalog_top.classList.remove('fixed');
            catalog_top.style.top = header_additionally_height + 'px';

            if (breadcrumbs) {
                breadcrumbs.style.display = 'block';
            }
        }

        if (product_top) {
            product_top.classList.remove('fixed');
            product_top.style.top = 'auto';

            if (breadcrumbs) {
                breadcrumbs.style.display = 'block';
            }
        }

        if (service_menu) {
            service_menu.classList.remove('fixed');
            service_menu.style.top = 'auto';

            if (breadcrumbs) {
                breadcrumbs.style.display = 'block';
            }
        }

        if (top_menu) {
            top_menu.classList.remove('fixed');
            top_menu.style.top = 'auto';

            if (breadcrumbs) {
                breadcrumbs.style.display = 'block';
            }
        }
    }
})

window.addEventListener('resize', function () {
    header_additionally_height = header_additionally.getBoundingClientRect().height;
    if (catalog_top && catalog_top.classList.contains('fixed')) {
        catalog_top.style.top = header_additionally_height + 'px';
    }

    if (product_top && product_top.classList.contains('fixed')) {
        product_top.style.top = header_additionally_height + 'px';
    }

    if (service_menu && service_menu.classList.contains('fixed')) {
        service_menu.style.top = header_additionally_height + 'px';
    }

    if (top_menu && top_menu.classList.contains('fixed')) {
        top_menu.style.top = header_additionally_height + 'px';
    }
})

// Динамічний адаптив
let da_elements = document.querySelectorAll('[data-da]');
let parents_original = [];
let attr_elements = [];
let attr_element = [];
let match_media = [];

for (let item of da_elements) {
    let parentChildren = item.parentElement.children;
    for (let i = 0; i < parentChildren.length; i++) {
        if (parentChildren[i] == item) {
            parents_original.push({
                'parent': item.parentElement,
                'index': i
            })
        }
    }
}

for (let item of da_elements) {
    attr_elements.push(item.dataset.da);
}

for (let i = 0; i < attr_elements.length; i++) {
    match_media.push(window.matchMedia("(max-width:" + attr_elements[i].split(', ')[2] + "px)"));
    match_media[i].addEventListener('change', dinamic_adapt);
}

function dinamic_adapt() {
    for (let i = 0; i < match_media.length; i++) {
        if (match_media[i].matches) {
            attr_element = attr_elements[i].split(', ');
            document.querySelector(`.${attr_element[0]}`).insertBefore(da_elements[i], document.querySelector(`.${attr_element[0]}`).children[attr_element[1]]);
        } else {
            parents_original[i].parent.insertBefore(da_elements[i], parents_original[i].parent.children[parents_original[i].index]);
        }
    }
}
dinamic_adapt();

