// Input File
let input_files = document.querySelectorAll('.file__input');

if (input_files) {
    input_files.forEach(function (input_file) {
        input_file.addEventListener('change', function (event) {
            let parent_form = this.closest('form');
            let file_previews = parent_form.querySelector('.file-previews');
            let file_body = parent_form.querySelector('.file__body');
            let file_message = parent_form.querySelector('.file__message');

            let file_preview = document.createElement('div');
            file_preview.classList.add('file-preview');

            let file_name = document.createElement('span');
            file_name.textContent = input_file.files[0].name + ' - готово';

            let file_delete = document.createElement('a');
            file_delete.setAttribute('href', '');
            file_delete.classList.add('close-file-preview');
            let file_delete_text = document.createElement('span');
            file_delete_text.textContent = 'Видалити';
            file_delete.prepend(file_delete_text);

            file_preview.append(file_name);
            file_preview.append(file_delete);
            file_previews.append(file_preview);

            hideFileBlock(parent_form);

            // Delete File Preview
            let close_file_btns = document.querySelectorAll('.close-file-preview');

            if (close_file_btns) {
                close_file_btns.forEach(function (close_file_btn) {
                    close_file_btn.addEventListener('click', function (event) {
                        event.preventDefault();

                        close_file_btn.parentElement.remove();
                        hideFileBlock(parent_form);
                    })
                })
            }

            function hideFileBlock(form) {
                let files = form.querySelectorAll('.file-preview');

                if (files && files.length >= 5) {
                    file_body.classList.add('hide');
                    file_message.classList.add('show');
                } else {
                    file_body.classList.remove('hide');
                    file_message.classList.remove('show');
                }
            }
        })
    })
}


