// Compare Parameters
let compare_parameters = document.querySelectorAll('.parameters-header-compare__input');
let compare_paremeters_list = document.querySelector('.parameters-list-compare');

if (compare_parameters) {
    compare_parameters.forEach(function (compare_parameter) {
        compare_parameter.addEventListener('click', function (event) {
            if (this.checked && compare_paremeters_list) {
                let compare_paremeters_rows = compare_paremeters_list.querySelectorAll('tr');

                if (this.classList.contains('identical')) {
                    if (compare_paremeters_rows) {
                        let counter = -1;

                        compare_paremeters_rows.forEach(function (tr) {
                            for (let i = 1; i < tr.children.length; i++) {
                                if (tr.children[i].firstElementChild) {
                                    for (let j = 1; j < tr.children.length; j++) {
                                        if (tr.children[j].firstElementChild && tr.children[i].firstElementChild.textContent == tr.children[j].firstElementChild.textContent) {
                                            counter++;
                                        }
                                    }
                                }

                                if (counter > 0) {

                                    tr.children[i].classList.add('similar');
                                }

                                counter = -1;
                            }
                        })
                    }

                } else if (this.classList.contains('differences')) {
                    if (compare_paremeters_rows) {
                        let counter = -1;

                        compare_paremeters_rows.forEach(function (tr) {
                            for (let i = 1; i < tr.children.length; i++) {
                                if (tr.children[i].firstElementChild) {
                                    for (let j = 1; j < tr.children.length; j++) {
                                        if (tr.children[j].firstElementChild && tr.children[i].firstElementChild.textContent == tr.children[j].firstElementChild.textContent) {
                                            counter++;
                                        }
                                    }
                                }

                                if (counter === 0) {

                                    tr.children[i].classList.add('different');
                                }

                                counter = -1;
                            }
                        })
                    }
                }
            } else if (this.checked == '' && compare_paremeters_list) {
                console.log('yes');
                let compare_paremeters_rows = compare_paremeters_list.querySelectorAll('tr');

                if (compare_paremeters_rows) {
                    if (this.classList.contains('identical')) {
                        compare_paremeters_rows.forEach(function (tr) {
                            for (let i = 1; i < tr.children.length; i++) {
                                tr.children[i].classList.remove('similar');
                            }
                        })
                    } else if (this.classList.contains('differences')) {
                        compare_paremeters_rows.forEach(function (tr) {
                            for (let i = 1; i < tr.children.length; i++) {
                                tr.children[i].classList.remove('different');
                            }
                        })
                    }
                }
            }
        })
    })
}


